let http,fs,url,os;_ec3‍.w("http",[["default",["http"],function(v){http=v}]]);_ec3‍.w("fs",[["default",["fs"],function(v){fs=v}]]);_ec3‍.w("url",[["default",["url"],function(v){url=v}]]);_ec3‍.w("os",[["default",["os"],function(v){os=v}]]);




let ip;
const port = 28000;

const httpserver = http.createServer((req, res) => {
    const date = new Date();
    const reqUrlString = req.url;

    if(req.method === "GET"){
        const get = '[' + date.getFullYear() + '-' + 
        (date.getMonth() + 1) + '-' + date.getDate() + ' ' +  
        date.getHours() + ':'+ date.getMinutes() +  ':'+ date.getSeconds() + 
        '] New GET request from: ' + req.socket.remoteAddress;

        _ec3‍.g.console.info(get);
        const urlObject = url.parse(reqUrlString, true, false);

        let fileName = urlObject.pathname;
        fileName = fileName.substr(1);

        fs.readFile(fileName, {encoding:'utf-8', flag: 'r'}, (error, data) => {
            if(!error){
                res.writeHead(200, 
                    {
                        'Access-Control-Allow-Origin':'*'
                    }
            );
                res.end(data);
            }
            else{
                res.writeHead(404, {'Access-Control-Allow-Origin':'*'});
                res.end(get);
            }
        });
    }
    else if(req.method === 'POST'){
        const ipre = req.socket.remoteAddress.splice(0,6);
        _ec3‍.g.console.info('[' + date.getFullYear() +'-' + 
                    (date.getMonth() + 1) + '-' + date.getDate() + ' ' +  
                    date.getHours() + ':'+ date.getMinutes() +  ':'+ date.getSeconds() + 
                    '] New POST request from: ' + ipre);
        
        const pathName = url.parse(reqUrlString, true, false).pathname;
        if('/data' == pathName){
            let body = '';
        
            req.on('data', chunk => {
                body += chunk;
            });
     
            req.on('end', () => {
                const postData = JSON.parse(body);
    
                const jsonContent = JSON.stringify(postData);

                fs.writeFile('./data/mesa_' + postData.fileName + '.json', jsonContent, 'utf-8', error => {if(error)_ec3‍.g.console.error(error)});
                
                res.writeHead(200, {'Access-Control-Allow-Origin':'*'});
                res.end("Ficheiro escrito com sucesso para: /data");
            })
        }
    }
})

const ipAdress = os.networkInterfaces();
Object.keys(ipAdress).forEach(ifName =>{
    let alias = 0;
    ipAdress[ifName].forEach(iface =>{
        if('IPv4' !== iface.family || iface.internal !== false)
            return;
        
        if(alias >= 1)
            _ec3‍.g.console.log(ifName + ':' + alias, iface.address);
        else
           ip = iface.address;
        ++alias;
    })
})

httpserver.listen(port);
_ec3‍.g.console.info("Http web server listening at " + ip + ':' + port + '.');